import React, { useState, useEffect, useCallback } from "react"; // add useState, useEffect
// ... keep other imports

export default function Graph({ productName = "Audience" }) { // optional prop for productName
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [loading, setLoading] = useState(true); // loading state

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:5000/datatrail/product/${productName}`)
      .then((res) => res.json())
      .then((data) => {
        // data should be [{view: "", input: ""}, ...]
        const nodeIds = [...new Set(data.flatMap((d) => [d.view, d.input]))];

        const rawNodes = nodeIds.map((id) => ({
          id,
          data: { label: id },
          position: { x: 0, y: 0 },
          style: {
            padding: 10,
            borderRadius: 8,
            background:
              "linear-gradient(135deg,rgb(165, 228, 244) 0%,rgb(207, 171, 243) 100%)",
            color: "rgb(10, 2, 1)",
            fontSize: 18,
            fontWeight: 600,
            minWidth: 370,
            height: 100,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            overflowX: "auto",
            whiteSpace: "nowrap",
            cursor: "grab",
          },
        }));

        const rawEdges = data.map((d) => ({
          id: `e-${d.input}->${d.view}`,
          source: d.input,
          target: d.view,
          animated: false,
          style: { stroke: "grey", strokeWidth: 2 },
          markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
        }));

        const { nodes: layoutedNodes, edges: layoutedEdges } = getLayoutedElements(
          rawNodes,
          rawEdges
        );

        setNodes(layoutedNodes);
        setEdges(layoutedEdges);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, [productName, setEdges]);

  const parentMap = buildParentMap(edges);

  const onNodeClick = useCallback(
    (_, node) => {
      const ancestorEdges = getAllAncestors(node.id, parentMap);

      setEdges((eds) =>
        eds.map((e) => {
          const isHighlighted = ancestorEdges.some(
            (ae) => ae.source === e.source && ae.target === e.target
          );

          if (isHighlighted) {
            return {
              ...e,
              animated: true,
              style: {
                stroke: "red",
                strokeWidth: 4,
                strokeDasharray: "5 5",
              },
              markerEnd: { type: MarkerType.ArrowClosed, color: "violet" },
            };
          }

          return {
            ...e,
            animated: false,
            style: { stroke: "grey", strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
          };
        })
      );
    },
    [setEdges, parentMap]
  );

  if (loading) {
    return (
      <div style={{ padding: 20, fontSize: 20, fontWeight: 600 }}>
        Rendering Graph...
      </div>
    );
  }

  return (
    <div style={{ width: "100vw", height: "81vh" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={onNodeClick}
        minZoom={0.3}
        maxZoom={5}
        fitView
        nodesDraggable
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
