import React, { useState } from "react";
import Selector from "./Selector";
import Graph from "./Graph";

export default function App() {
  const [filters, setFilters] = useState({
    productName: "",
    startDate: "",
    startHour: "",
    endDate: "",
    endHour: "",
  });
  const [showGraph, setShowGraph] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleLoadGraph = () => {
    // Validate all fields
    if (
      !filters.productName ||
      !filters.startDate ||
      !filters.startHour ||
      !filters.endDate ||
      !filters.endHour
    ) {
      alert("Please select all filters before loading graph!");
      return;
    }

    // Set loading state
    setLoading(true);
    setShowGraph(false);

    // Simulate async graph rendering delay (e.g. API call or heavy plot)
    setTimeout(() => {
      setLoading(false);
      setShowGraph(true);
    }, 2000); // 2 sec delay
  };

  return (
    <div>
      <Selector filters={filters} setFilters={setFilters} onLoadGraph={handleLoadGraph} />

      {loading && (
        <div
          style={{
            marginTop: "20px",
            textAlign: "center",
            fontSize: "18px",
            fontWeight: "600",
            color: "#007bff",
            animation: "pulse 1.5s infinite",
          }}
        >
          ⚡ Rendering Graph... Dynamic Visualization Loading ⚡
        </div>
      )}

      {showGraph && <Graph filters={filters} />}

      {/* Inline CSS animation */}
      <style>
        {`
          @keyframes pulse {
            0% { opacity: 0.4; }
            50% { opacity: 1; }
            100% { opacity: 0.4; }
          }
        `}
      </style>
    </div>
  );
}
