import React, { useState, useEffect, useCallback } from "react";
import ReactFlow, {
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "reactflow";
import dagre from "dagre";
import "reactflow/dist/style.css";

// ---- DAGRE SETUP ----
const dagreGraph = new dagre.graphlib.Graph();
dagreGraph.setDefaultEdgeLabel(() => ({}));

const nodeWidth = 320;
const nodeHeight = 80;

// ---- DAGRE LAYOUT ----
const getLayoutedElements = (nodes, edges) => {
  dagreGraph.setGraph({ rankdir: "TB", ranksep: 350, nodesep: 80 });

  nodes.forEach((node) => {
    dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
  });

  edges.forEach((edge) => {
    dagreGraph.setEdge(edge.source, edge.target);
  });

  dagre.layout(dagreGraph);

  nodes.forEach((node) => {
    const nodeWithPos = dagreGraph.node(node.id);
    if (!nodeWithPos) return;
    node.position = {
      x: nodeWithPos.x - nodeWidth / 2,
      y: nodeWithPos.y - nodeHeight / 2,
    };
    node.draggable = true;
  });

  return { nodes, edges };
};

// ---- UTILS FOR HIGHLIGHT ----
const buildParentMap = (edges) => {
  const parentMap = {};
  edges.forEach((e) => {
    if (!parentMap[e.target]) parentMap[e.target] = [];
    parentMap[e.target].push(e.source);
  });
  return parentMap;
};

const getAllAncestors = (nodeId, parentMap) => {
  const visited = new Set();
  const stack = [nodeId];
  const ancestorEdges = [];

  while (stack.length > 0) {
    const current = stack.pop();
    const parents = parentMap[current] || [];
    parents.forEach((p) => {
      ancestorEdges.push({ source: p, target: current });
      if (!visited.has(p)) {
        visited.add(p);
        stack.push(p);
      }
    });
  }

  return ancestorEdges;
};

// ---- GRAPH COMPONENT ----
export default function Graph({ productName, startDate, endDate }) {
  const [dependencies, setDependencies] = useState([]);
  const [loading, setLoading] = useState(true);

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // ðŸ”´ Health check results map (jobName â†’ absentEntries)
  const [healthCheckMap, setHealthCheckMap] = useState({});

  const parentMap = buildParentMap(edges);

  const onNodeClick = useCallback(
    (_, node) => {
      const ancestorEdges = getAllAncestors(node.id, parentMap);

      setEdges((eds) =>
        eds.map((e) => {
          const isHighlighted = ancestorEdges.some(
            (ae) => ae.source === e.source && ae.target === e.target
          );

          if (isHighlighted) {
            return {
              ...e,
              animated: true,
              style: {
                stroke: "red",
                strokeWidth: 6,
                strokeDasharray: "5 5",
              },
              markerEnd: { type: MarkerType.ArrowClosed, color: "violet" },
            };
          }

          return {
            ...e,
            animated: false,
            style: { stroke: "grey", strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
          };
        })
      );
    },
    [setEdges, parentMap]
  );

  // Fetch API data when productName changes
  useEffect(() => {
    if (!productName || !startDate || !endDate) return;

    setLoading(true);
    setNodes([]);
    setEdges([]);
    setHealthCheckMap({});

    // First fetch dependencies
    fetch(
      `http://localhost:5000/datatrail/productMapping/${productName}?startDate=${startDate}&endDate=${endDate}`
    )
      .then((res) => res.json())
      .then((data) => {
        setDependencies(data.dependencies || []);

        // ðŸ”´ Then fetch health check in parallel
        return fetch(
          `http://localhost:5000/datatrail/healthCheck/${productName}`,
          {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ startDate, endDate }),
          }
        );
      })
      .then((res) => res.json())
      .then((hcData) => {
        // Convert results into a lookup map
        const hcMap = {};
        hcData.results?.forEach((job) => {
          hcMap[job.jobName] = job.absentEntries || [];
        });
        setHealthCheckMap(hcMap);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, [productName, startDate, endDate, setNodes, setEdges]);

  // Build nodes & edges once dependencies are fetched
  useEffect(() => {
    if (dependencies.length === 0) return;

    const nodeIds = [
      ...new Set(
        dependencies
          .flatMap((d) => [d.view, d.input || d.raw_input])
          .filter((id) => id !== undefined && id !== null && id !== "")
      ),
    ];

    const rawNodes = nodeIds.map((id) => {
      const isRawInput = dependencies.some(
        (d) => d.raw_input && d.raw_input === id
      );

      // ðŸ”´ Check health map for this node
      const isUnhealthy = healthCheckMap[id]?.length > 0;

      return {
        id,
        data: { label: id },
        position: { x: 0, y: 0 },
        style: {
          padding: 10,
          borderRadius: 8,
          background: isUnhealthy
            ? "linear-gradient(135deg,rgb(242, 190, 199) 0%,rgb(238, 16, 24) 100%)"
            : isRawInput
            ? "linear-gradient(135deg,rgba(232, 234, 236, 0.27) 0%,rgba(29, 29, 29, 0.31) 100%)"
            : "linear-gradient(135deg,rgb(221, 242, 223) 0%,rgb(7, 188, 76) 100%)",
          color: "black",
          fontSize: 18,
          fontWeight: 600,
          minWidth: 370,
          height: 100,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          overflowX: "auto",
          whiteSpace: "nowrap",
          cursor: "grab",
        },
      };
    });

    const rawEdges = dependencies.map((d) => ({
      id: `e-${d.input || d.raw_input}->${d.view}`,
      source: d.input || d.raw_input,
      target: d.view,
      animated: false,
      style: { stroke: "grey", strokeWidth: 2 },
      markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
    }));

    const { nodes: initialNodes, edges: initialEdges } = getLayoutedElements(
      rawNodes,
      rawEdges
    );

    setNodes(initialNodes);
    setEdges(initialEdges);
  }, [dependencies, healthCheckMap, setNodes, setEdges]);

  // if (loading) {
  //   return (
  //     <div style={{ padding: 20, fontSize: 20, fontWeight: 600 }}>
  //       Rendering Graph...
  //     </div>
  //   );
  // }

  if (loading) {
    return (
      <div className="flex items-center justify-center w-full h-[81vh] bg-gray-50">
        <div className="relative w-48 h-48">
          {/* Node 1 */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 w-8 h-8 bg-blue-400 rounded-full animate-pulse" />
          {/* Node 2 */}
          <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-8 h-8 bg-green-400 rounded-full animate-pulse" />
          {/* Node 3 */}
          <div className="absolute left-0 top-1/2 -translate-y-1/2 w-8 h-8 bg-purple-400 rounded-full animate-pulse" />
          {/* Node 4 */}
          <div className="absolute right-0 top-1/2 -translate-y-1/2 w-8 h-8 bg-pink-400 rounded-full animate-pulse" />

          {/* Edges (animated lines) */}
          <div className="absolute top-6 left-1/2 w-px h-[calc(50%-16px)] bg-gray-400 animate-pulse" />
          <div className="absolute bottom-6 left-1/2 w-px h-[calc(50%-16px)] bg-gray-400 animate-pulse" />
          <div className="absolute left-6 top-1/2 h-px w-[calc(50%-16px)] bg-gray-400 animate-pulse" />
          <div className="absolute right-6 top-1/2 h-px w-[calc(50%-16px)] bg-gray-400 animate-pulse" />
        </div>
      </div>
    );
  }


  return (
    <div style={{ width: "100vw", height: "81vh" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={onNodeClick}
        minZoom={0.3}
        maxZoom={5}
        fitView
        nodesDraggable
      >
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
}
