import React, { useState } from "react";
import Selector from "./Input";
import Graph from "./Graph";

export default function App() {
  const [filters, setFilters] = useState({
    startDate: "",
    startHour: "",
    endDate: "",
    endHour: "",
    productName: "",
  });

  const [showGraph, setShowGraph] = useState(false);
  const [error, setError] = useState("");

  const handleLoadGraph = () => {
    // validation
    if (
      filters.productName &&
      filters.startDate &&
      filters.startHour !== "" &&
      filters.endDate &&
      filters.endHour !== ""
    ) {
      setError("");
      setShowGraph(true);
    } else {
      setError("⚠️ Please select all fields before loading the graph.");
      setShowGraph(false);
    }
  };

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex", flexDirection: "column" }}>
      {/* Selector with button */}
      <Selector filters={filters} setFilters={setFilters} onLoadGraph={handleLoadGraph} />

      {/* Error below selector */}
      {error && <div style={{ color: "red", padding: "8px 20px" }}>{error}</div>}

      {/* Graph area */}
      <div style={{ flex: 1 }}>
        {showGraph ? (
          <Graph />
        ) : (
          <div style={{ padding: "20px" }}>
            Select filters and click <b>Load Graph</b> to view results.
          </div>
        )}
      </div>
    </div>
  );
}
