import React from "react";
import Graph from "react-vis-network-graph";
import "vis-network/styles/vis-network.css";

const graph = {
  nodes: [
    { id: 1, label: "Root", color: "red" },
    { id: 2, label: "Child A", color: "blue" },
    { id: 3, label: "Child B", color: "green" },
    { id: 4, label: "Grandchild", color: "orange" }
  ],
  edges: [
    { from: 1, to: 2 },
    { from: 1, to: 3 },
    { from: 3, to: 4 }
  ]
};

const options = {
  layout: {
    hierarchical: {
      direction: "UD",   // Up → Down
      sortMethod: "directed"
    }
  },
  edges: {
    arrows: {
      to: { enabled: true, scaleFactor: 1 }
    }
  },
  physics: {
    enabled: true
  },
  interaction: { hover: true }
};

export default function App() {
  return (
    <div style={{ height: "600px" }}>
      <Graph
        graph={graph}
        options={options}
        events={{
          select: ({ nodes, edges }) =>
            console.log("Selected:", nodes, edges),
        }}
      />
    </div>
  );
}
