import React, { useState } from "react";
import Selector from "./Input";
import Graph from "./Graph";

export default function App() {
  const [filters, setFilters] = useState({
    startDate: "",
    startHour: "",
    endDate: "",
    endHour: "",
    productName: "",
  });

  const [showGraph, setShowGraph] = useState(false);
  const [error, setError] = useState("");

  const handleLoadGraph = () => {
    // Validation: all fields must be filled
    if (
      filters.productName &&
      filters.startDate &&
      filters.startHour !== "" &&
      filters.endDate &&
      filters.endHour !== ""
    ) {
      setError("");
      setShowGraph(true);
    } else {
      setError("⚠️ Please select all fields before loading the graph.");
      setShowGraph(false);
    }
  };

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Top selector bar */}
      <Selector filters={filters} setFilters={setFilters} />

      {/* Load button */}
      <div style={{ padding: "10px", background: "#fafafa", borderBottom: "1px solid #ddd" }}>
        <button
          onClick={handleLoadGraph}
          style={{
            padding: "8px 16px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            fontSize: "14px",
          }}
        >
          Load Graph
        </button>
        {error && <span style={{ color: "red", marginLeft: "15px" }}>{error}</span>}
      </div>

      {/* Conditionally render graph */}
      <div style={{ flex: 1 }}>
        {showGraph ? (
          <Graph />
        ) : (
          <div style={{ padding: "20px" }}>
            Select all filters and click <b>Load Graph</b> to view results.
          </div>
        )}
      </div>
    </div>
  );
}
