import React, { useState, useEffect, useCallback } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "reactflow";
import dagre from "dagre";
import "reactflow/dist/style.css";

// ---- DAGRE SETUP ----
const dagreGraph = new dagre.graphlib.Graph();
dagreGraph.setDefaultEdgeLabel(() => ({}));

const nodeWidth = 320;
const nodeHeight = 80;

// ---- DAGRE LAYOUT ----
const getLayoutedElements = (nodes, edges) => {
  dagreGraph.setGraph({ rankdir: "TB", ranksep: 250, nodesep: 80 });

  nodes.forEach((node) => {
    dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
  });

  edges.forEach((edge) => {
    dagreGraph.setEdge(edge.source, edge.target);
  });

  dagre.layout(dagreGraph);

  nodes.forEach((node) => {
    const nodeWithPos = dagreGraph.node(node.id);
    if (!nodeWithPos) return;
    node.position = {
      x: nodeWithPos.x - nodeWidth / 2,
      y: nodeWithPos.y - nodeHeight / 2,
    };
    node.draggable = true;
  });

  return { nodes, edges };
};

// ---- UTILS FOR HIGHLIGHT ----
const buildParentMap = (edges) => {
  const parentMap = {};
  edges.forEach((e) => {
    if (!parentMap[e.target]) parentMap[e.target] = [];
    parentMap[e.target].push(e.source);
  });
  return parentMap;
};

const getAllAncestors = (nodeId, parentMap) => {
  const visited = new Set();
  const stack = [nodeId];
  const ancestorEdges = [];

  while (stack.length > 0) {
    const current = stack.pop();
    const parents = parentMap[current] || [];
    parents.forEach((p) => {
      ancestorEdges.push({ source: p, target: current });
      if (!visited.has(p)) {
        visited.add(p);
        stack.push(p);
      }
    });
  }

  return ancestorEdges;
};

// ---- GRAPH COMPONENT ----
export default function Graph({ initialProduct = "Audience" }) {
  const [dependencies, setDependencies] = useState([]);
  const [loading, setLoading] = useState(false);

  // Selected product from UI input
  const [selectedProduct, setSelectedProduct] = useState(initialProduct);

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const parentMap = buildParentMap(edges);

  const onNodeClick = useCallback(
    (_, node) => {
      const ancestorEdges = getAllAncestors(node.id, parentMap);

      setEdges((eds) =>
        eds.map((e) => {
          const isHighlighted = ancestorEdges.some(
            (ae) => ae.source === e.source && ae.target === e.target
          );

          if (isHighlighted) {
            return {
              ...e,
              animated: true,
              style: {
                stroke: "red",
                strokeWidth: 6,
                strokeDasharray: "5 5",
              },
              markerEnd: { type: MarkerType.ArrowClosed, color: "violet" },
            };
          }

          return {
            ...e,
            animated: false,
            style: { stroke: "grey", strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
          };
        })
      );
    },
    [setEdges, parentMap]
  );

  // ---- Load graph button handler ----
  const loadGraph = () => {
    setLoading(true);
    fetch(`http://localhost:5000/datatrail/product/${selectedProduct}`)
      .then((res) => res.json())
      .then((data) => {
        setDependencies(data.dependencies || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  };

  // Build nodes & edges when dependencies change
  useEffect(() => {
    if (dependencies.length === 0) return;

    const nodeIds = [
      ...new Set(
        dependencies
          .flatMap((d) => [d.view, d.input || d.raw_input])
          .filter((id) => id !== undefined && id !== null && id !== "")
      ),
    ];

    const rawNodes = nodeIds.map((id) => ({
      id,
      data: { label: id },
      position: { x: 0, y: 0 },
      style: {
        padding: 10,
        borderRadius: 8,
        background:
          "linear-gradient(135deg,rgb(165, 228, 244) 0%,rgb(207, 171, 243) 100%)",
        color: "rgb(10, 2, 1)",
        fontSize: 18,
        fontWeight: 600,
        minWidth: 370,
        height: 100,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        overflowX: "auto",
        whiteSpace: "nowrap",
        cursor: "grab",
      },
    }));

    const rawEdges = dependencies.map((d) => ({
      id: `e-${d.input || d.raw_input}->${d.view}`,
      source: d.input || d.raw_input,
      target: d.view,
      animated: false,
      style: { stroke: "grey", strokeWidth: 2 },
      markerEnd: { type: MarkerType.ArrowClosed, color: "grey" },
    }));

    const { nodes: initialNodes, edges: initialEdges } = getLayoutedElements(
      rawNodes,
      rawEdges
    );

    setNodes(initialNodes);
    setEdges(initialEdges);
  }, [dependencies, setNodes, setEdges]);

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      {/* UI input for product selection */}
      <div style={{ margin: 10 }}>
        <input
          type="text"
          value={selectedProduct}
          onChange={(e) => setSelectedProduct(e.target.value)}
          placeholder="Enter Product Name"
          style={{ padding: 5, fontSize: 16 }}
        />
        <button
          onClick={loadGraph}
          style={{ marginLeft: 10, padding: "5px 15px", fontSize: 16 }}
        >
          Load Graph
        </button>
      </div>

      {loading ? (
        <div style={{ padding: 20, fontSize: 20, fontWeight: 600 }}>
          Rendering Graph...
        </div>
      ) : (
        <div style={{ width: "100%", height: "81vh" }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onNodeClick={onNodeClick}
            minZoom={0.3}
            maxZoom={5}
            fitView
            nodesDraggable
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </div>
      )}
    </div>
  );
}
