import React, { useEffect, useRef } from "react";

export default function DynamicSVGPlaceholder() {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = svgRef.current;
    if (!svg) return;

    const circles = [];
    const numCircles = 30;
    const width = svg.clientWidth;
    const height = svg.clientHeight;

    // Initialize circles with random positions
    for (let i = 0; i < numCircles; i++) {
      circles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: 5 + Math.random() * 5,
        dx: (Math.random() - 0.5) * 0.5,
        dy: (Math.random() - 0.5) * 0.5,
      });
    }

    let animationFrameId;

    const animate = () => {
      const svgChildren = svg.children;
      for (let i = 0; i < numCircles; i++) {
        let c = circles[i];

        // Update position
        c.x += c.dx;
        c.y += c.dy;

        // Bounce on edges
        if (c.x < 0 || c.x > width) c.dx *= -1;
        if (c.y < 0 || c.y > height) c.dy *= -1;

        // Update SVG circle element
        const circleEl = svgChildren[i];
        circleEl.setAttribute("cx", c.x);
        circleEl.setAttribute("cy", c.y);
      }

      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  return (
    <svg
      ref={svgRef}
      style={{ width: "100%", height: "81vh", background: "#f9f9f9" }}
    >
      {Array.from({ length: 30 }).map((_, i) => (
        <circle key={i} cx={0} cy={0} r={5} fill="rgba(100, 149, 237, 0.7)" />
      ))}
    </svg>
  );
}
