import React, { useState } from "react";
import Selector from "./Input";
import Graph from "./Graph";

export default function App() {
  const [filters, setFilters] = useState({
    startDate: "",
    startHour: "",
    endDate: "",
    endHour: "",
    productName: "",
  });

  const [showGraph, setShowGraph] = useState(false);
  const [loading, setLoading] = useState(false); // new loading state
  const [error, setError] = useState("");

  const handleLoadGraph = () => {
    if (
      filters.productName &&
      filters.startDate &&
      filters.startHour !== "" &&
      filters.endDate &&
      filters.endHour !== ""
    ) {
      setError("");
      setLoading(true);
      setShowGraph(true); // immediately show Graph, loading message shows until Graph triggers ready
    } else {
      setError("⚠️ Please select all fields before loading the graph.");
      setShowGraph(false);
    }
  };

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex", flexDirection: "column" }}>
      {/* Selector with button */}
      <Selector filters={filters} setFilters={setFilters} onLoadGraph={handleLoadGraph} />

      {/* Error below selector */}
      {error && <div style={{ color: "red", padding: "8px 20px" }}>{error}</div>}

      {/* Graph area */}
      <div style={{ flex: 1, display: "flex", justifyContent: "center", alignItems: "center" }}>
        {loading && (
          <div style={{ fontSize: "18px", fontWeight: "600", color: "#007bff", textAlign: "center", animation: "pulse 1.5s infinite" }}>
            ⚡ Rendering Graph... Dynamic Visualization ⚡
          </div>
        )}

        {showGraph && !loading && <Graph setLoading={setLoading} />}

        {!showGraph && !loading && !error && (
          <div>Select filters and click <b>Load Graph</b> to view results.</div>
        )}
      </div>

      {/* Inline CSS animation */}
      <style>
        {`
          @keyframes pulse {
            0% { opacity: 0.4; }
            50% { opacity: 1; }
            100% { opacity: 0.4; }
          }
        `}
      </style>
    </div>
  );
}
