import React, { useEffect, useState } from "react";

export default function Header() {
  const [colorIndex, setColorIndex] = useState(0);
  const colors = ["#6a0dad", "#ff4500", "#1e90ff", "#32cd32", "#ff1493"];

  // Change color dynamically every 1 second
  useEffect(() => {
    const interval = setInterval(() => {
      setColorIndex((prev) => (prev + 1) % colors.length);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      style={{
        width: "100%",
        padding: "15px 40px",
        background: "linear-gradient(90deg, #f0f4ff, #e6f0ff)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
      }}
    >
      {/* Logo */}
      <img
        src="/dataTrail.png" // public folder path
        alt="DataTrail Logo"
        style={{ width: 50, height: 50, marginRight: 15, borderRadius: 8 }}
      />

      {/* App name */}
      <span
        style={{
          fontSize: "36px",
          fontWeight: "bold",
          color: colors[colorIndex],
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          transition: "color 0.5s ease",
        }}
      >
        DataTrail
      </span>
    </div>
  );
}
