import React, { useState } from "react";
import Selector from "./Input.js";
import Graph from "./Graph.js";

export default function App() {
  const [filters, setFilters] = useState({
    startDate: "",
    startHour: 0,
    endDate: "",
    endHour: 0,
    productName: "",
  });

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex", flexDirection: "column" }}>
      {/* Top selector bar */}
      <Selector filters={filters} setFilters={setFilters} />

      {/* Conditionally render graph */}
      <div style={{ flex: 1 }}>
        {filters.productName === "Audience" ? (
          <Graph />
        ) : (
          <div style={{ padding: "20px" }}>Please select Product = Audience to view the graph.</div>
        )}
      </div>
    </div>
  );
}
